message:
  nicer string handling in usage
modified:
  tools/ctdb_control.c           ctdb_control.c-20070426122705-9ehj1l5lu2gn9kuj-1
=== modified file 'tools/ctdb_control.c'
--- a/tools/ctdb_control.c	2007-05-02 01:06:58 +0000
+++ b/tools/ctdb_control.c	2007-05-02 03:29:03 +0000
@@ -31,25 +31,26 @@
  */
 static void usage(void)
 {
-	printf("Usage: ctdb_control [options] \n");
-	printf("\nControls:\n");
-	printf("  ping\n");
-	printf("  process-exists            see if a process exists\n");
-	printf("  status                    show ctdb status on a node\n");
-	printf("  statusreset               reset status on a node\n");
-	printf("  debug              set ctdb debug level on a node\n");
-	printf("  debuglevel                         display ctdb debug levels\n");
-	printf("  getvnnmap                     display ctdb vnnmap\n");
-	printf("  setvnnmap    *\n");
-	printf("  getdbmap                      lists databases on a node\n");
-	printf("  getnodemap                    lists nodes known to a ctdb daemon\n");
-	printf("  catdb                   lists all keys in a remote tdb\n");
-	printf("  cpdb         lists all keys in a remote tdb\n");
-	printf("  setdmaster     sets new dmaster for all records in the database\n");
-	printf("  cleardb                 deletes all records in a db\n");
-	printf("  getrecmode                    get recovery mode\n");
-	printf("  setrecmode              set recovery mode\n");
-	printf("  recover                       recover the cluster\n");
+	printf(
+		"Usage: ctdb_control [options] \n"
+		"\nControls:\n"
+		"  ping\n"
+		"  process-exists            see if a process exists\n"
+		"  status                    show ctdb status on a node\n"
+		"  statusreset               reset status on a node\n"
+		"  debug              set ctdb debug level on a node\n"
+		"  debuglevel                         display ctdb debug levels\n"
+		"  getvnnmap                     display ctdb vnnmap\n"
+		"  setvnnmap    *\n"
+		"  getdbmap                      lists databases on a node\n"
+		"  getnodemap                    lists nodes known to a ctdb daemon\n"
+		"  catdb                   lists all keys in a remote tdb\n"
+		"  cpdb         lists all keys in a remote tdb\n"
+		"  setdmaster     sets new dmaster for all records in the database\n"
+		"  cleardb                 deletes all records in a db\n"
+		"  getrecmode                    get recovery mode\n"
+		"  setrecmode              set recovery mode\n"
+		"  recover                       recover the cluster\n");
 	exit(1);
 }
