>>
>> sub getDates {
>>         for (1 .. 7) {
>>                 $time -= 24*60*60;
>>                 my @date = (localtime($time))[3 .. 5];
>>                 push @days, (sprintf '%02d', $date[0]);
>>                 push @months,(sprintf '%02d',$date[1] + 1);
>>                 push @years, $date[2] + 1900;
>>                 return;
>> }
>>
